jQuery(document).ready(function($){for(var a=800,t=$("#stars"),r=800,n=0;a>n;n++){var e=$("<div/>").addClass("star");t.append(e)}$(".star").each(function(){var a=$(this),t=.2+1*Math.random(),n=r+300*Math.random();a.css({transformOrigin:"0 0 "+n+"px",transform:" translate3d(0,0,-"+n+"px) rotateY("+360*Math.random()+"deg) rotateX("+-50*Math.random()+"deg) scale("+t+","+t+")"})}),$(".thumbnail a").on("click",function(a){a.preventDefault()})});